app.get("/bookings", (req, res) => {
  db.query("SELECT * FROM bookings", (err, results) => {
    if (err) return res.status(500).send(err);
    res.json(results);
  });
});

app.post("/bookings", (req, res) => {
  const { name, date } = req.body;
  db.query(
    "INSERT INTO bookings (name, date) VALUES (?, ?)",
    [name, date],
    (err) => {
      if (err) return res.status(500).send(err);
      res.sendStatus(201);
    }
  );
});
